import Player from '../player'
import '../style/player/definition.scss'

let definition = function () {
  let player = this; let util = Player.util; let sniffer = Player.sniffer
  let ul = util.createDom('xg-definition', '', {tabindex: 3}, 'xgplayer-definition'); let root = player.controls
  if (sniffer.device === 'mobile') {
    player.config.definitionActive = 'click'
  }
  function canplayDefiFunc () {
    let list = player.definitionList
    let tmp = ['<ul>']; let src = player.config.url; let a = document.createElement('a')
    if (player.switchURL) {
      ['mp4', 'hls', '__flv__', 'dash'].every(item => {
        if (player[item]) {
          if (player[item].url) {
            a.href = player[item].url
          }
          if (item === '__flv__') {
            if (player[item]._options) {
              a.href = player[item]._options.url
            } else {
              a.href = player[item]._mediaDataSource.url
            }
          }
          if(item === 'hls') {
            a.href = player[item].originUrl || player[item].url
            src = a.href
          }
          src = a.href
          return false
        } else {
          return true
        }
      })
    } else {
      src = player.currentSrc || player.src
    }
    list.forEach(item => {
      a.href = item.url
      if (player.dash) {
        tmp.push(`<li url='${item.url}' cname='${item.name}' class='${item.selected ? 'definition' : ''}'>${item.name}</li>`)
      } else {
        tmp.push(`<li url='${item.url}' cname='${item.name}' class='${a.href === src ? 'definition' : ''}'>${item.name}</li>`)
      }
    })
    let cursrc = list.filter(item => {
      a.href = item.url
      if (player.dash) {
        return item.selected === true
      } else {
        return a.href === src
      }
    })
    tmp.push(`</ul><p class='name'>${(cursrc[0] || {name: ''}).name}</p>`)
    let urlInRoot = root.querySelector('.xgplayer-definition')
    if (urlInRoot) {
      urlInRoot.innerHTML = tmp.join('')
      let cur = urlInRoot.querySelector('.name')
      if (!player.config.definitionActive || player.config.definitionActive === 'hover') {
        cur.addEventListener('mouseenter', (e) => {
          e.preventDefault()
          e.stopPropagation()
          util.addClass(player.root, 'xgplayer-definition-active')
          urlInRoot.focus()
        })
      }
    } else {
      ul.innerHTML = tmp.join('')
      let cur = ul.querySelector('.name')
      if (!player.config.definitionActive || player.config.definitionActive === 'hover') {
        cur.addEventListener('mouseenter', (e) => {
          e.preventDefault()
          e.stopPropagation()
          util.addClass(player.root, 'xgplayer-definition-active')
          ul.focus()
        })
      }
      root.appendChild(ul)
    }
  }
  function resourceReadyFunc (list) {
    player.definitionList = list
    if (list && list instanceof Array && list.length > 1) {
      util.addClass(player.root, 'xgplayer-is-definition')
      util.once(player, 'canplay', canplayDefiFunc, 'destroy')
    }
  }
  util.on(player, 'resourceReady', resourceReadyFunc, 'destroy');

  ['touchend', 'click'].forEach(item => {
    ul.addEventListener(item, function (e) {
      e.preventDefault()
      e.stopPropagation()
      let list = player.definitionList
      let li = e.target || e.srcElement; let a = document.createElement('a')
      if (li && li.tagName.toLocaleLowerCase() === 'li') {
        let from = ''
        let to = li.getAttribute('cname')
        player.emit('beforeDefinitionChange', a.href)
        Array.prototype.forEach.call(li.parentNode.childNodes, item => {
          if (util.hasClass(item, 'definition')) {
            from = item.getAttribute('cname')
            util.removeClass(item, 'definition')
          }
        })
        if (player.dash) {
          list.forEach(item => {
            item.selected = false
            if (item.name === li.innerHTML) {
              item.selected = true
            }
          })
        }

        util.addClass(li, 'definition')
        li.parentNode.nextSibling.innerHTML = `${li.getAttribute('cname')}`
        a.href = li.getAttribute('url')
        if (player.switchURL) {
          let curRUL = document.createElement('a');
          ['mp4', 'hls', '__flv__', 'dash'].every(item => {
            if (player[item]) {
              if (player[item].url) {
                curRUL.href = player[item].url
              }
              if (item === '__flv__') {
                if (player[item]._options) {
                  curRUL.href = player[item]._options.url
                } else {
                  curRUL.href = player[item]._mediaDataSource.url
                }
              }
              if(item === 'hls') {
                curRUL.href = player[item].originUrl || player[item].url
              }
              return false
            } else {
              return true
            }
          })
          if (curRUL.href !== a.href && !player.ended) {
            player.switchURL(a.href)
          }
        } else {
          if (player['hls']) {
            let curRUL = document.createElement('a')
            curRUL = player['hls'].url
          }
          if (a.href !== player.currentSrc) {
            let curTime = player.currentTime; let paused = player.paused
            if (!player.ended) {
              player.src = a.href
              util.once(player, 'canplay', () => {
                player.currentTime = curTime
                if (!paused) {
                  player.play()
                }
              })
            }
          }
        }
        player.emit('definitionChange', {from, to})
        if (sniffer.device === 'mobile') {
          util.removeClass(player.root, 'xgplayer-definition-active')
        }
      } else if (player.config.definitionActive === 'click' && li && (li.tagName.toLocaleLowerCase() === 'p' || li.tagName.toLocaleLowerCase() === 'em')) {
        util.addClass(player.root, 'xgplayer-definition-active')
        ul.focus()
      }
      player.emit('focus')
    }, false)
  })

  ul.addEventListener('mouseleave', (e) => {
    e.preventDefault()
    e.stopPropagation()
    util.removeClass(player.root, 'xgplayer-definition-active')
  })
}

export default {
  name: 'definition',
  method: definition
}