import Player from './player'

import Danmu from './control/danmu'
import Flex from './control/flex'
import Fullscreen from './control/fullscreen'
import I18n from './control/i18n'
import Loading from './control/loading'
import LocalPreview from './control/localPreview'
import Mobile from './control/mobile'
import Pc from './control/pc'
import Play from './control/play'
import PlaybackRate from './control/playbackRate'
import Poster from './control/poster'
import Time from './control/time'
import Keyboard from './control/keyboard'

let controlLst = [Danmu, Flex, Fullscreen, I18n, Loading, LocalPreview, Mobile, Pc, Play, PlaybackRate, Poster, Time, Keyboard]
controlLst.forEach(function(control) {
    Player.install(control.name, control.method)
})
import './style/index.scss'

export default Player
